uint32_t dac_buffer_load(dac_core core, uint32_t start_address) {

	uint32_t no_of_samples;
	uint32_t index_i, index_q;
	uint32_t index_mem = 0;

	no_of_samples = sizeof(square_lut) / sizeof(typeof(square_lut[0]));
	printf("\n\r");
	for (index_i = 0; index_i < no_of_samples; index_i++) {
		 /* Phase shifted by 90 degree */
		index_q = (index_i + 256) % no_of_samples;

		switch (core.no_of_channels) {
			case 1:
				ad_reg_write_16(start_address + index_mem * 2, square_lut[index_i]-1);
				index_mem += 1;
				break;
			case 2:

				ad_reg_write_16(start_address + (index_mem + 0) * 2, square_lut[index_i]-1);
				ad_reg_write_16(start_address + (index_mem + 1) * 2, square_lut[index_i]-1);
				
				index_mem += 2;
				break;
			case 4:
				ad_reg_write_16(start_address + (index_mem + 0) * 2,  square_lut[index_i]);
				ad_reg_write_16(start_address + (index_mem + 1) * 2,  square_lut[index_q]);
				ad_reg_write_16(start_address + (index_mem + 2) * 2,  square_lut[index_i]);
				ad_reg_write_16(start_address + (index_mem + 3) * 2,  square_lut[index_q]);
				index_mem += 4;
				break;
			default:
				ad_printf("Unsupported mode.\n\r");
				return -1;
		}
	}

	ad_dcache_flush();

	return (core.no_of_channels * no_of_samples);
}

