entity Mem_Buffer is
    generic (ADDRESS_BITS : integer := 128;
             Data_size    : integer := 10);
    Port ( mem_out_0  : out STD_LOGIC_VECTOR ( (ADDRESS_BITS/2)-1 downto 0);
           mem_out_1  : out STD_LOGIC_VECTOR ( (ADDRESS_BITS/2)-1 downto 0);
           enable     : in  STD_LOGIC;
           single_shot: in  STD_LOGIC;
           val_info   : in  STD_LOGIC;
           clk_in     : in  STD_LOGIC);
end Mem_Buffer;

architecture Behavioral of Mem_Buffer is

    subtype WORD is std_logic_vector(ADDRESS_BITS-1 downto 0);
    type    MEM  is array(0 to ((2**Data_size) -1)) of WORD;
    signal s_mem : MEM := (
