00",	X"00000000000000000000000000000000",	X"00000000000000000000000000000000",	X"00000000000000000000000000000000",	X"00000000000000000000000000000000",	X"00000000000000000000000000000000",	X"00000000000000000000000000000000",	X"00000000000000000000000000000000",	X"00000000000000000000000000000000",	X"00000000000000000000000000000000",	X"00000000000000000000000000000000",	X"00000000000000000000000000000000",	X"00000000000000000000000000000000",	X"00000000000000000000000000000000",	X"00000000000000000000000000000000",	X"00000000000000000000000000000000",	X"00000000000000000000000000000000",	X"00000000000000000000000000000000",	X"00000000000000000000000000000000",	X"00000000000000000000000000000000",	X"00000000000000000000000000000000",	X"00000000000000000000000000000000",	X"00000000000000000000000000000000",	X"00000000000000000000000000000000",	X"00000000000000000000000000000000",	X"00000000000000000000000000000000",	X"00000000000000000000000000000000",	X"00000000000000000000000000000000",	X"00000000000000000000000000000000",	X"00000000000000000000000000000000",	X"00000000000000000000000000000000",	X"00000000000000000000000000000000",	X"00000000000000000000000000000000",	X"00000000000000000000000000000000",	X"00000000000000000000000000000000",	X"00000000000000000000000000000000",	X"00000000000000000000000000000000",	X"00000000000000000000000000000000",	X"00000000000000000000000000000000",	X"00000000000000000000000000000000",	X"00000000000000000000000000000000",	X"00000000000000000000000000000000",	X"00000000000000000000000000000000",	X"00000000000000000000000000000000",	X"00000000000000000000000000000000",	X"00000000000000000000000000000000",	X"00000000000000000000000000000000",	X"00000000000000000000000000000000",	X"00000000000000000000000000000000",	X"00000000000000000000000000000000",	X"00000000000000000000000000000000",	X"00000000000000000000000000000000");
    
    -- Main signals for memory management
    signal s_counter : unsigned(data_size-1 downto 0) := (others => '0');
    signal s_start : STD_LOGIC := '0';
    
    -- Implementation of an Edge Detector to substitute function "rising/falling_edge"
    signal reg2 : STD_LOGIC;
    signal reg1 : STD_LOGIC;
    signal edge : STD_LOGIC;
    
begin

    process(clk_in, enable, val_info, s_start, edge)
        begin  
            if edge = '1' then 
                s_start <= '1';
            end if;
            
            if rising_edge(clk_in) then
                    
                    if val_info = '1' and enable = '1' then 
                        ---CHECK IF COUNTER reached its limit
--                        if s_counter > (2**Data_size -1) then
--                           
--                        end if;
                           
                        -- Parse signal in memory
                        mem_out_0 <= s_mem(to_integer(s_counter))( (ADDRESS_BITS/2)-1 downto 0);
                        mem_out_1 <= s_mem(to_integer(s_counter))( (ADDRESS_BITS-1) downto (ADDRESS_BITS/2));
                        
                        -- 
                        if s_counter = ((2**Data_size) -1) then
                             s_start <= '0';        -- DEACTIVATE START SEQUENCE
                             s_counter <= (others => '0');
                        end if;
                        
                        if s_start = '1' then 
                            s_counter <= s_counter + 1;
                        end if;
                        
                    end if;
               end if;
        end process;
    
    -- Single shot rising edge detection
sim_clk: 
    process (s_start, clk_in)
        begin 
              ---IF single shot is active HIGH , set up the memory start sequence
            if(rising_edge(clk_in)) then   
                reg1 <= single_shot;                 --START ONLY ACTIVE IN SINGLE SHOT TRANSITION
                reg2 <= reg1;
            end if;
    end process;
    
    edge <= reg1 and not reg2;

end Behavioral;
